open System

let vert = "|"
let horiz = "-"
let corner = "+"
let empty = " "

// TODO: use an accumulator instead of filthy globals
let mutable collection:list<string> = []
let mutable visited = 0

let inputToGrid (grid:string) =
        grid.Split("\n") |> Array.map (fun row -> row.ToCharArray() |> Array.map (fun c -> c.ToString()))

let nextCell pos direction (grid:string[][]) =
    let x,y = pos
    let vx,vy = direction
    let result = grid.[y+vy].[x+vx]
    result

let turnCorner (pos:int*int) (direction:(int*int))(grid:string[][]) =
    let ny,nx = direction // turning means swapping horizontal for vertical movement
    let x,y = pos
    if (grid.Length > (y+ny) && grid.[0].Length > (x+nx)) && nextCell pos (nx,ny) grid <> " " 
    then 
        (pos,(nx,ny)) 
    else 
        (pos, (nx * -1, ny * -1))

let rec visitCell (state:((int*int)*(int*int))) (grid:string[][]) =
    let pos,direction = state
    let x,y = pos
    let dx,dy = direction
    
    let next = nextCell pos direction grid
    printfn "%s" next
    visited <- visited + 1

    // TODO: can probably simplify this
    match next with
    | n when n = vert || n = horiz -> visitCell ((x+dx,y+dy),direction) grid
    | n when n = corner -> visitCell (turnCorner (x+dx,y+dy) direction grid) grid
    | n when n = empty -> state
    // else it must be a letter, collect and continue
    | _ -> 
        collection <- collection@[next] // slower to do this than to prepend and reverse at the end
        visitCell ((x+dx, y+dy),direction) grid

